import React, { Component } from 'react'
import PropTypes from 'prop-types'

import './index.css'

const loadingGif = 'data:image/gif;base64,'



class Image extends Component {
  constructor (props) {
    super(props)

    this.state = {
      src: loadingGif
    }

  }

  static defaultProps = {
    src: ''
  }

  preload () {

    let img = new Image()
    img.src = this.props.src
    img.onload = () => {
      this.setState({
        src: this.props.src
      })
    }

  }

  render () {

    return (
      <img src={this.state.src} />
    )
  }

  componentDidMount () {
    this.preload()
  }
}

if (process.env.NODE_ENV === 'development') {
  Image.propTypes = {
    src: PropTypes.string.isRequired,
  }
}

export default Image
